import React from "react";

function Results({ results }) {
  return (
    <div>
      <h2>Results</h2>
      <p><strong>Best Performing Algorithm:</strong> {results.best_algorithm}</p>
      <p><strong>Generative Model Output:</strong> {results.generative_output}</p>
      <img src={`data:image/png;base64,${results.graph}`} alt="Algorithm Performance" />

      <h3>Retrieved Documents</h3>
      {results.documents.map((doc, idx) => (
        <div key={idx} style={{ marginBottom: "20px", padding: "10px", border: "1px solid #ccc" }}>
          <h4>Document {idx + 1}</h4>
          <p>{doc.substring(0, 500)}...</p> {/* Show first 500 characters */}
          <a href={results.links[idx]} target="_blank" rel="noopener noreferrer">
            Read Full Document
          </a>
          <div style={{ marginTop: "10px" }}>
            <p><strong>Naive Bayes Score:</strong> {results.naive_bayes_scores[idx]}</p>
            <p><strong>BM25 Score:</strong> {results.bm25_scores[idx]}</p>
            <p><strong>BSBI Score:</strong> {results.bsbi_scores[idx]}</p>
          </div>
        </div>
      ))}
    </div>
  );
}

export default Results;
